const puppeteer = require('puppeteer');

async function fetchMonthlySummaryValues() {
  const url = 'https://egauge75568.d.egauge.net/616DC/l/en_GB/stats.html'; // Replace with the actual URL of your eGauge meter UI
  const selector = '#monthlySummaryBody > tr:nth-child(3) > td:nth-child(2) > span > a'; // Replace with the actual selector for the monthly summary values

  try {
    const browser = await puppeteer.launch();
    const page = await browser.newPage();
    await page.goto(url);

    // Wait for the selector to be available in the DOM
    await page.waitForSelector(selector);

    // Fetch all the monthly summary values
    const monthlySummaryValues = await page.$$eval(selector, elements =>
      elements.map(element => element.textContent)
    );

    // Do something with the monthly summary values
    console.log('Monthly Summary Values:', monthlySummaryValues);

    await browser.close();
  } catch (error) {
    console.error('Error:', error);
  }
}

fetchMonthlySummaryValues();

const puppeteer = require('puppeteer');

async function fetchMonthlySummaryValues() {
  const url = 'https://egauge75568.d.egauge.net/616DC/l/en_GB/stats.html'; // Replace with the actual URL of your eGauge meter UI
  const tasks = [
    { selector: 'td.year.right_border', label: 'Year' },
    //{ selector: '#monthlySummaryBody > tr: nth - child(4) > td.year.right_border', label: 'year' },
    { selector: '#monthlySummaryBody > tr:nth-child(3) > td:nth-child(2) > span > a', label:'jan' },
    { selector: '#monthlySummaryBody > tr:nth-child(3) > td:nth-child(3) > span > a', label:'feb' },
    { selector: '#monthlySummaryBody > tr:nth-child(3) > td:nth-child(4) > span > a', label:'mar' },
    { selector: '#monthlySummaryBody > tr:nth-child(3) > td:nth-child(5) > span > a', label:'apr' },
    { selector: '#monthlySummaryBody > tr:nth-child(3) > td:nth-child(6) > span > a', label:'may' },
    { selector: '#monthlySummaryBody > tr:nth-child(3) > td:nth-child(7) > span > a', label:'jun' },
    //{ selector: '#monthlySummaryBody > tr:nth-child(3) > td:nth-child(8) > span > a', label:'jul' },
    //{ selector: '#monthlySummaryBody > tr:nth-child(3) > td:nth-child(9) > span > a', label:'aug' },
    //{ selector: '#monthlySummaryBody > tr:nth-child(3) > td:nth-child(10) > span > a', label:'sep' },
    //{ selector: '#monthlySummaryBody > tr:nth-child(3) > td:nth-child(11) > span > a', label:'oct' },
    //{ selector: '#monthlySummaryBody > tr:nth-child(3) > td:nth-child(12) > span > a', label:'nov' },
    //{ selector: '#monthlySummaryBody > tr:nth-child(3) > td:nth-child(13) > span > a', label:'dec' },
    { selector: '#monthlySummaryBody > tr:nth-child(3) > td.total.left_border > span > a', label:'Total' },
    // Add more tasks as needed
  ];

  try {
    const browser = await puppeteer.launch();
    const page = await browser.newPage();
    await page.goto(url);

    for (const task of tasks) {
      // Wait for the selector to be available in the DOM
      await page.waitForSelector(task.selector);

      // Fetch the value
      const value = await page.$eval(task.selector, element => element.textContent);

      // Do something with the value
      console.log(`${task.label}:`, value);
    }

    await browser.close();
  } catch (error) {
    console.error('Error:', error);
  }
}

fetchMonthlySummaryValues();

