const puppeteer = require('puppeteer');
const fs = require('fs');

async function getDownloadUrl(url, username, password, sn, startdate, enddate) {
    const browser = await puppeteer.launch({
        headless: false,
        args: ['--disable-web-security'],
        defaultViewport: null,
    });
    const page = await browser.newPage();
    await page.setViewport({ width: 1200, height: 800 });

    try {
        await page.goto(url, { waitUntil: 'networkidle0' });

        await page.waitForSelector('input[type="text"]');
        await page.type('input[type="text"]', username);
        await page.type('input[type="password"]', password);

        await Promise.all([
            page.click('button.el-button.login-click.el-button--primary'),
            page.waitForNavigation({ waitUntil: 'networkidle0' }),
        ]);

        await page.click(
            '#app > div > section > aside > div > div.sidebar-container > div.el-scrollbar > div.scrollbar-wrapper.el-scrollbar__wrap > div > ul > div:nth-child(10)'
        );

        await page.waitForSelector('input[type="text"]');
        await page.type('input[type="text"]', sn);

        await page.waitForSelector('input[placeholder="Start Date"]');
        await page.type('input[placeholder="Start Date"]', startdate);

        await page.waitForSelector('input[placeholder="End Date"]');
        await page.type('input[placeholder="End Date"]', enddate);

        await page.click('button.el-button.el-button--success.el-button--mini');

        const response = await page.waitForResponse((response) => {
            return response.url().includes('https://www.foxesscloud.com/c/v0/device/report/query');
        }, { timeout: 60000 });

        const responseData = await response.json();
        const downloadUrl = responseData.result.downloadUrl;

        return downloadUrl; // Resolve the Promise with the download URL
    } finally {
        await browser.close();
    }
}

