  // Click the dropdown menu to open it
  await page.click('#app > div > section > aside > div > div.sidebar-container > div.el-scrollbar > div.scrollbar-wrapper.el-scrollbar__wrap > div > ul > div:nth-child(10)');

  await page.waitForSelector('input[type="text"]');
